import React, { useState } from "react";
import "antd/dist/antd.css";
import "./App.css";
import {
  SearchOutlined,
  ArrowUpOutlined,
  ArrowDownOutlined,
} from "@ant-design/icons";
import {
  Input,
  Row,
  Col,
  Button,
  Form,
  Typography,
  PageHeader,
  Statistic,
  Card,
} from "antd";
const { Text, Title } = Typography;
var mailcheck = require("mailcheck");
import {
  defaultDomains,
  defaultTopLevelDomains,
  defaultSecondLevelDomains,
} from "./emailDomains";
import InfoCard from "./infoCard";

function App() {
  const [email, setEmail] = useState("");
  const [error, setError] = useState("");
  const [suggestion, setSuggestion] = useState(null);
  const [checking, setChecking] = useState(false);
  const [info, setInfo] = useState(null);

  function onSubmit() {
    if (email === "") {
      if (error !== "") {
        setError("Stop that!");
      } else {
        setError("Please enter an email");
      }

      return;
    }

    setChecking(true);
    mailcheck.run({
      email: email,
      domains: defaultDomains, // optional
      topLevelDomains: defaultTopLevelDomains, // optional
      secondLevelDomains: defaultSecondLevelDomains, // optional
      suggested: function (suggestion) {
        // callback code
        console.log(suggestion, "x");
        setChecking(false);
        if (suggestion) {
          setSuggestion(suggestion);
        }
      },
      empty: function (x) {
        console.log(x);
        setError("No suggestion for this email");
        setChecking(false);
        setInfo("What does this mean?");
      },
    });
  }

  return (
    <>
      <PageHeader
        className="site-page-header"
        title="Email"
        subTitle="checker"
      />
      <div className="App">
        <Row>
          <Col
            xs={{ span: 20, offset: 2 }}
            md={{ span: 8, offset: 8 }}
            lg={{ span: 6, offset: 9 }}
          >
            <Title style={{ marginBottom: "10px" }} level={4}>
              Enter an email
            </Title>
            <Form>
              <Input
                allowClear
                onPressEnter={onSubmit}
                disabled={checking}
                placeholder="Enter email"
                onChange={(val) => {
                  if (error !== "") {
                    setError("");
                  }
                  setEmail(val.currentTarget.value);
                }}
                value={email}
              />
              {suggestion ? (
                <>
                  <span>Did you mean</span>
                  <Button
                    type="link"
                    onClick={() => {
                      setEmail(suggestion.full);
                      setSuggestion(null);
                    }}
                  >
                    {suggestion.full}
                  </Button>
                </>
              ) : null}

              {error !== "" ? <Text type="danger">{error}</Text> : null}
              {info !== null && email !== "" && error !== "" ? (
                <Button type="link" onClick={() => {}}>
                  What does this mean?
                </Button>
              ) : null}

              <Button
                disabled={checking}
                onClick={onSubmit}
                style={{ marginTop: "25px", width: "100%" }}
                type="primary"
                icon={<SearchOutlined />}
              >
                Check
              </Button>
            </Form>
          </Col>
        </Row>
        <InfoCard />
      </div>
    </>
  );
}

export default App;
