import React, { useState } from "react";
import "antd/dist/antd.css";
import "./App.css";
import { SearchOutlined } from "@ant-design/icons";
import { Input, Row, Col, Button, Form, Typography, PageHeader } from "antd";
const { Text, Title } = Typography;
var mailcheck = require("mailcheck");

function App() {
  const [email, setEmail] = useState("");
  const [error, setError] = useState("");
  const [suggestion, setSuggestion] = useState(null);
  const [checking, setChecking] = useState(false);

  var defaultDomains = [
    "msn.com",
    "bellsouth.net",
    "telus.net",
    "comcast.net",
    "optusnet.com.au",
    "earthlink.net",
    "qq.com",
    "sky.com",
    "icloud.com",
    "mac.com",
    "sympatico.ca",
    "googlemail.com",
    "att.net",
    "xtra.co.nz",
    "web.de",
    "cox.net",
    "gmail.com",
    "ymail.com",
    "aim.com",
    "rogers.com",
    "verizon.net",
    "rocketmail.com",
    "google.com",
    "optonline.net",
    "sbcglobal.net",
    "aol.com",
    "me.com",
    "btinternet.com",
    "charter.net",
    "shaw.ca",
  ];

  var defaultSecondLevelDomains = [
    "yahoo",
    "hotmail",
    "mail",
    "live",
    "outlook",
    "gmx",
  ];

  var defaultTopLevelDomains = [
    "com",
    "com.au",
    "com.tw",
    "ca",
    "co.nz",
    "co.uk",
    "de",
    "fr",
    "it",
    "ru",
    "net",
    "org",
    "edu",
    "gov",
    "jp",
    "nl",
    "kr",
    "se",
    "eu",
    "ie",
    "co.il",
    "us",
    "at",
    "be",
    "dk",
    "hk",
    "es",
    "gr",
    "ch",
    "no",
    "cz",
    "in",
    "net",
    "net.au",
    "info",
    "biz",
    "mil",
    "co.jp",
    "sg",
    "hu",
  ];

  function onSubmit() {
    if (email === "") {
      setError("Please enter an email");
      return;
    }

    setChecking(true);
    mailcheck.run({
      email: email,
      domains: defaultDomains, // optional
      topLevelDomains: defaultTopLevelDomains, // optional
      secondLevelDomains: defaultSecondLevelDomains, // optional
      suggested: function (suggestion) {
        // callback code
        console.log(suggestion, "x");
        setChecking(false);
        if (suggestion) {
          setSuggestion(suggestion);
        }
      },
      empty: function (x) {
        console.log(x);
        setError("No suggestion for this email");
        setChecking(false);
      },
    });
  }

  return (
    <>
      <PageHeader
        className="site-page-header"
        title="Email"
        subTitle="checker"
      />
      <div className="App">
        <Row>
          <Col xs={{ span: 20, offset: 2 }} lg={{ span: 4, offset: 10 }}>
            <Title style={{ marginBottom: "50px" }} level={3}>
              Enter an email
            </Title>
            <Form>
              <Input
                disabled={checking}
                placeholder="Enter email"
                onChange={(val) => {
                  if (error !== "") {
                    setError("");
                  }
                  setEmail(val.currentTarget.value);
                }}
                value={email}
              />
              {suggestion ? (
                <>
                  <span>Did you mean</span>
                  <Button
                    type="link"
                    onClick={() => {
                      setEmail(suggestion.full);
                      setSuggestion(null);
                    }}
                  >
                    {suggestion.full}
                  </Button>
                </>
              ) : null}
              {error !== "" ? <Text type="danger">{error}</Text> : null}
              <Button
                disabled={checking}
                onClick={onSubmit}
                style={{ marginTop: "25px", width: "100%" }}
                type="primary"
                icon={<SearchOutlined />}
              >
                Check
              </Button>
            </Form>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default App;
